import React, { Component } from 'react';
import { Row, Col, Card, CardHeader, CardFooter, Button, FormFeedback, CardBody, Form, FormGroup, Label, Input, FormText, InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { Formik } from 'formik';
import * as Yup from 'yup'
import '../Forms/ValidationForms/ValidationForms.css'
// React select
import states from '../Forms/AdvancedForms/data/states';
import Select from 'react-select';
import 'react-select/dist/react-select.min.css';


const options = states.US;

const initialValues = {
  realmId: [],
  countryId: [],
  healthAreaName: ""
}

const validationSchema = function (values) {
  return Yup.object().shape({
    realmId: Yup.string()
      .required('Please select realm.'),
    countryId: Yup.string()
      .required('Please select country'),
    healthAreaName: Yup.string()
      .required('Please enter health area')
  })
}

const validate = (getValidationSchema) => {
  return (values) => {
    const validationSchema = getValidationSchema(values)
    try {
      validationSchema.validateSync(values, { abortEarly: false })
      return {}
    } catch (error) {
      return getErrorsFromValidationError(error)
    }
  }
}

const getErrorsFromValidationError = (validationError) => {
  const FIRST_ERROR = 0
  return validationError.inner.reduce((errors, error) => {
    return {
      ...errors,
      [error.path]: error.errors[FIRST_ERROR],
    }
  }, {})
}

const onSubmit = (values, { setSubmitting, setErrors }) => {
  setTimeout(() => {
    alert(JSON.stringify(values, null, 2))
    // console.log('User has been successfully saved!', values)
    setSubmitting(false)
  }, 2000)
}

class AddHealthArea extends Component {
  constructor(props) {
    super(props);
    this.saveRealmChanges = this.saveRealmChanges.bind(this);
    this.saveCountryChanges = this.saveCountryChanges.bind(this);
    this.state = {
      realmId: [],
      countryId: [],
      healthAreaName: ""
    };
  }

  touchAll(setTouched, errors) {
    setTouched({
      realmId: true,
      countryId: true,
      healthAreaName: true
    }
    )
    this.validateForm(errors)
  }
  validateForm(errors) {
    this.findFirstError('simpleForm', (fieldName) => {
      return Boolean(errors[fieldName])
    })
  }
  findFirstError(formName, hasError) {
    const form = document.forms[formName]
    for (let i = 0; i < form.length; i++) {
      if (hasError(form[i].name)) {
        form[i].focus()
        break
      }
    }
  }

  saveRealmChanges(value1) {
    this.setState({ value1 });
  }

  saveCountryChanges(value2) {
    this.setState({ value2 });
  }

  render() {

    return (
      <div className="animated fadeIn">
        <Row>
          <Col sm={12} md={6} style={{ flexBasis: 'auto' }}>
            <Card>
              <CardHeader>
                <i className="icon-note"></i><strong>Add Health Area</strong>{' '}
              </CardHeader>
              <CardBody>
                <Formik
                  initialValues={initialValues}
                  validate={validate(validationSchema)}
                  onSubmit={onSubmit}
                  render={
                    ({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                      isValid,
                      setTouched
                    }) => (
                        <Form onSubmit={handleSubmit} noValidate name='simpleForm'>
                          <FormGroup>
                            <Label>Realm</Label>
                            <Select
                              name="realmId"
                              id="realmId"
                              options={options}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              valid={!errors.realmId}
                              invalid={touched.realmId && !!errors.realmId}
                              required
                            />
                            <FormFeedback>{errors.realmId}</FormFeedback>
                          </FormGroup>
                          <FormGroup>
                            <Label>Country</Label>
                            <Select
                              name="countryId"
                              id="countryId"
                              valid={!errors.countryId}
                              invalid={touched.countryId && !!errors.countryId}
                              options={options}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              multi
                              required
                            />
                            <FormFeedback>{errors.countryId}</FormFeedback>
                          </FormGroup>
                          <FormGroup>
                            <Label for="healthArea">Health Area</Label>
                            <Input type="text"
                              name="healthAreaName"
                              id="healthAreaName"
                              valid={!errors.healthAreaName}
                              invalid={touched.healthAreaName && !!errors.healthAreaName}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              required />
                            <FormFeedback>{errors.healthAreaName}</FormFeedback>
                          </FormGroup>
                          <FormGroup>
                            <Button type="submit" color="primary" className="mr-1" disabled={isSubmitting || !isValid}>{isSubmitting ? 'Wait...' : 'Submit'}</Button>
                            <Button type="button" color="success" className="mr-1" onClick={() => this.touchAll(setTouched, errors)} disabled={isValid}>Validate</Button>
                            <Button type="reset" color="danger" className="mr-1">Cancel</Button>
                          </FormGroup>
                        </Form>
                      )} />
              </CardBody>
              <CardFooter>
                <Button type="submit" size="md" color="primary"><i className="fa fa-dot-circle-o"></i> Submit</Button>
                <Button type="reset" size="md" color="danger"><i className="fa fa-ban"></i> Cancel</Button>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default AddHealthArea;
