import * as JsStore from 'jsstore';
import { IDataBase, DATA_TYPE, ITable } from 'jsstore';
import CryptoJS from 'crypto-js'
import { SECRET_KEY } from '../Constants.js'
// This will ensure that we are using only one instance. 
// Otherwise due to multiple instance multiple worker will be created.
export const idbCon = new JsStore.Connection();
export const dbname = 'fasp';


var dbName = 'JsStore_Demo';
function getDbSchema() {
    var tblDataSource = {
        name: 'dataSource',
        columns: {
            // Here "Id" is name of column 
            dataSourceId: { primaryKey: true },
            active: { notNull: true, dataType: "boolean" },
            dataSourceType: { notNull: true, dataType: "object" },
            label: { notNull: true, dataType: "obejct" }
        }
    };
    var db = {
        name: dbName,
        tables: [tblDataSource]
    }
    return db;
}

export function syncDataSource(json) {
    var database = getDbSchema();
    idbCon.initDb(database);
    console.log("in data source core")
    var newData = []
    for (var i = 0; i < json.length; i++) {
        console.log("in loop")
        newData[i] = {
            dataSourceId: json[i].dataSourceId,
            active: json[i].active,
            dataSourceType: json[i].dataSourceType,
            label: json[i].label
        }
    }
    console.log("new Data", newData)
    console.log(idbCon)
    idbCon.insert({
        into: "dataSource",
        upsert: true,
        values: [1, true, {}, {}], //you can insert multiple values at a time
    })
}

