import React, { Component } from 'react';
import { Row, Col, Card, CardHeader, CardFooter, Button, FormFeedback, CardBody, Form, FormGroup, Label, Input, FormText, InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { Formik } from 'formik';
import * as Yup from 'yup'
import '../Forms/ValidationForms/ValidationForms.css'
// React select
import states from '../Forms/AdvancedForms/data/states';
import Select from 'react-select';
import 'react-select/dist/react-select.min.css';

import HealthAreaService from "../../api/HealthAreaService";
import CountryService from "../../api/CountryService";

const initialValues = {
  realmId: [],
  countryId: [],
  healthAreaName: ""
}

const validationSchema = function (values) {
  return Yup.object().shape({
    realmId: Yup.string()
      .required('Please select realm.'),
    countryId: Yup.string()
      .required('Please select country'),
    healthAreaName: Yup.string()
      .required('Please enter health area')
  })
}

const validate = (getValidationSchema) => {
  return (values) => {
    const validationSchema = getValidationSchema(values)
    try {
      validationSchema.validateSync(values, { abortEarly: false })
      return {}
    } catch (error) {
      return getErrorsFromValidationError(error)
    }
  }
}

const getErrorsFromValidationError = (validationError) => {
  const FIRST_ERROR = 0
  return validationError.inner.reduce((errors, error) => {
    return {
      ...errors,
      [error.path]: error.errors[FIRST_ERROR],
    }
  }, {})
}

const onSubmit = (values, { setSubmitting, setErrors }) => {
  setTimeout(() => {
    alert(JSON.stringify(values, null, 2))
    // console.log('User has been successfully saved!', values)
    setSubmitting(false)
  }, 2000)
}

const commodities = [
  { value: "commodity1", label: "commodity1" },
  { value: "commodity2", label: "commodity2" }
];

class AddHealthArea extends Component {
  constructor(props) {
    super(props);
    this.state = {
      countries: [],
      realms: [],
      healthArea: {
        label: {

        },
        realm: {

        },
        realmCountryArray: {

        }
      },
      selCountries: [],
      message: ''
    }
    this.submitClicked = this.submitClicked.bind(this);
    this.cancelClicked = this.cancelClicked.bind(this);
    this.dataChange = this.dataChange.bind(this);
    this.getCountryListByRealmId = this.getCountryListByRealmId.bind(this);
  }

  dataChange(event) {
    console.log("handle change called===" + event);
    // const { name, value } = event.target;
    // this.setState({
    //   [name]: value,
    // });
    console.log("handle change works");
  };

  touchAll(setTouched, errors) {
    setTouched({
      realmId: true,
      countryId: true,
      healthAreaName: true
    }
    )
    this.validateForm(errors)
  }
  validateForm(errors) {
    this.findFirstError('simpleForm', (fieldName) => {
      return Boolean(errors[fieldName])
    })
  }
  findFirstError(formName, hasError) {
    const form = document.forms[formName]
    for (let i = 0; i < form.length; i++) {
      if (hasError(form[i].name)) {
        form[i].focus()
        break
      }
    }
  }

  componentDidMount() {
    CountryService.getRealmCountryList()
      .then(response => {
        console.log("country list---", response.data);
        this.setState({
          countries: response.data
        })
      }).catch(
        error => {
          switch (error.message) {
            case "Network Error":
              this.setState({
                message: error.message
              })
              break
            default:
              this.setState({
                message: error.response.data.message
              })
              break
          }
        }
      );
    HealthAreaService.getRealmList()
      .then(response => {
        console.log("realm list---", response.data);
        this.setState({
          realms: response.data
        })
      }).catch(
        error => {
          switch (error.message) {
            case "Network Error":
              this.setState({
                message: error.message
              })
              break
            default:
              this.setState({
                message: error.response.data.message
              })
              break
          }
        }
      );
  }

  render() {
    const { selCountries } = this.state;
    const { realms } = this.state;

    let realmList = realms.length > 0
      && realms.map((item, i) => {
        return (
          <option key={i} value={item.realmId}>
            {item.label.label_en}
          </option>
        )
      }, this);

    let countryList = selCountries.length > 0
      && selCountries.map((item, i) => {
        return (
          <option key={i} value={item.realmCountryId}>
            {item.country.label.label_en}
          </option>
        )
      }, this);
    return (
      <div className="animated fadeIn">
        <Row>
          <Col sm={12} md={6} style={{ flexBasis: 'auto' }}>
            <Card>
              <CardHeader>
                <i className="icon-note"></i><strong>Add Health Area</strong>{' '}
              </CardHeader>
              <CardBody>
                <Formik
                  initialValues={initialValues}
                  validate={validate(validationSchema)}
                  onSubmit={onSubmit}
                  render={
                    ({
                      values,
                      errors,
                      touched,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                      isValid,
                      setTouched
                    }) => (
                        <Form onSubmit={handleSubmit} noValidate name='simpleForm'>
                          <FormGroup>
                            <Label htmlFor="realmId">Realm</Label>
                            <Input
                              type="select"
                              name="realmId"
                              id="realmId"
                              bsSize="lg"
                              valid={!errors.realmId}
                              invalid={touched.realmId && !!errors.realmId}
                              onChange={e => {handleChange; this.dataChange(e) }}
                              onBlur={handleBlur}
                              required
                            >
                              <option value="0">Please select</option>
                              {realmList}
                            </Input>
                            <FormFeedback>{errors.realmId}</FormFeedback>
                          </FormGroup>
                          <FormGroup>
                            <Label>Country</Label>
                            <Input
                              type="select"
                              name="countryId"
                              id="countryId"
                              bsSize="lg"
                              valid={!errors.countryId}
                              invalid={touched.countryId && !!errors.countryId}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              required
                            >
                              <option value="0">Please select</option>
                              {countryList}
                            </Input>
                            <FormFeedback>{errors.countryId}</FormFeedback>
                          </FormGroup>
                          <FormGroup>
                            <Label for="healthArea">Health Area</Label>
                            <Input type="text"
                              name="healthAreaName"
                              id="healthAreaName"
                              valid={!errors.healthAreaName}
                              invalid={touched.healthAreaName && !!errors.healthAreaName}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              required />
                            <FormFeedback>{errors.healthAreaName}</FormFeedback>
                          </FormGroup>

                          <FormGroup>
                            {/* <Button type="submit" color="primary" className="mr-1" disabled={isSubmitting || !isValid}>{isSubmitting ? 'Wait...' : 'Submit'}</Button> */}
                            <Button type="button" color="success" className="mr-1" onClick={() => this.touchAll(setTouched, errors)} disabled={isValid}>Validate</Button>
                            <Button type="reset" color="danger" className="mr-1">Cancel</Button>
                          </FormGroup>
                        </Form>
                      )} />
              </CardBody>
              <CardFooter>
                {/* <Button type="submit" size="md" color="primary"><i className="fa fa-dot-circle-o"></i> Submit</Button> */}
                {/* <Button type="reset" size="md" color="danger"><i className="fa fa-ban"></i> Cancel</Button> */}
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
  getCountryListByRealmId(event) {
    let realmId = event.target.value;
    const selCountries = this.state.countries.filter(c => c.realm.realmId == realmId)
    this.setState({
      selCountries: selCountries
    });
  }
  submitClicked() {
    // if (navigator.onLine) {
    //   if (AuthenticationService.checkTypeOfSession()) {
    //     if ($("#healthAreaForm").valid()) {
    //       HealthAreaService.addHealthArea(this.state.healthArea)
    //         .then(response => {
    //           if (response.data.message != "Failed") {
    //             this.props.history.push(`/healthAreaList/${response.data.message}`)
    //           } else {
    //             this.setState({
    //               message: response.data.message
    //             })
    //           }
    //         })
    //         .catch(
    //           error => {
    //             switch (error.message) {
    //               case "Network Error":
    //                 this.setState({
    //                   message: error.message
    //                 })
    //                 break
    //               default:
    //                 this.setState({
    //                   message: error.response.data.message
    //                 })
    //                 break
    //             }
    //           }
    //         );
    //     }
    //   } else {
    //     alert("You can't change your session from online to offline or vice versa.");
    //   }
    // } else {
    //   alert("You must be Online.")
    // }
  }
  cancelClicked() {
    this.props.history.push(`/healthAreaList/` + "Action Canceled")
