import React, { Component } from 'react';
import {
    Card, CardBody, CardHeader,
    Label, Input, FormGroup,
    CardFooter, Button, Col, Form
    , FormFeedback
} from 'reactstrap';
import { Formik } from 'formik';
import * as Yup from 'yup';
import '../Forms/ValidationForms/ValidationForms.css';
import ProgramServcie from '../../api/ProgramService';
import getLabelText from '../../CommonComponent/getLabelText.js';
import Select from 'react-select';
import '../../../node_modules/react-select/dist/react-select.css';

const initialValues = {
    programId: ''
}

const validationSchema = function (values) {
    return Yup.object().shape({
        programId: Yup.string()
            .required('Please select program.')
    })
}

const validate = (getValidationSchema) => {
    return (values) => {
        const validationSchema = getValidationSchema(values)
        try {
            validationSchema.validateSync(values, { abortEarly: false })
            return {}
        } catch (error) {
            return getErrorsFromValidationError(error)
        }
    }
}

const getErrorsFromValidationError = (validationError) => {
    const FIRST_ERROR = 0
    return validationError.inner.reduce((errors, error) => {
        return {
            ...errors,
            [error.path]: error.errors[FIRST_ERROR],
        }
    }, {})
}

const onSubmit = (values, { setSubmitting, setErrors }) => {
    console.log("submit called---", values);
    setTimeout(() => {
        alert(JSON.stringify(values, null, 2))
        // console.log('User has been successfully saved!', values)
        setSubmitting(false)
    }, 2000)
}

export default class DownloadProgram extends Component {

    constructor() {
        super();
        this.state = {
            programList: []
        }
        this.updateFieldData = this.updateFieldData.bind(this);
    }

    componentDidMount() {
        const lan = 'en'
        ProgramServcie.getProgramList().then(response => {
            var json = response.data;
            var prgList = [];
            for (var i = 0; i < json.length; i++) {
                var programJson = { value: json[i].programId, label: getLabelText(json[i].label, lan) }
                prgList[i] = programJson;
            }
            this.setState({
                programList: prgList
            })
        })
            .catch(
                error => {
                    switch (error.message) {
                        case "Network Error":
                            this.setState({
                                message: error.message
                            })
                            break
                        default:
                            this.setState({
                                message: error.message
                            })
                            break
                    }
                }
            );


    }

    touchAll(setTouched, errors) {
        setTouched({
            programId: true
        }
        )
        this.validateForm(errors)
    }
    validateForm(errors) {
        this.findFirstError('simpleForm', (fieldName) => {
            return Boolean(errors[fieldName])
        })
    }
    findFirstError(formName, hasError) {
        const form = document.forms[formName]
        for (let i = 0; i < form.length; i++) {
            if (hasError(form[i].name)) {
                form[i].focus()
                break
            }
        }
    }

    updateFieldData(value) {
        this.setState({ programId: value });
    }

    render() {
        // const lan = 'en';
        // const { programList } = this.state;
        // const prgList=[]
        // let programs = programList.length > 0
        //     && programList.map((item, i) => {

        //     }, this);
        return (
            <>
                <Col xs="12" sm="8">
                    <Card>
                        <Formik
                            initialValues={initialValues}
                            validate={validate(validationSchema)}
                            onSubmit={onSubmit}
                            render={
                                ({
                                    values,
                                    errors,
                                    touched,
                                    handleChange,
                                    handleBlur,
                                    handleSubmit,
                                    isSubmitting,
                                    isValid,
                                    setTouched
                                }) => (
                                        <Form onSubmit={handleSubmit} noValidate name='simpleForm'>
                                            <CardHeader>
                                                <strong>Download Program Data</strong>
                                            </CardHeader>
                                            <CardBody>
                                                <FormGroup >
                                                    <Col md="3">
                                                        <Label htmlFor="select">Select Program</Label>
                                                    </Col>
                                                    <Col xs="12" md="9">
                                                        <Select
                                                            name="programId"
                                                            id="programId"
                                                            bsSize="lg"
                                                            options={this.state.programList}
                                                            valid={!errors.programId}
                                                            invalid={touched.programId && !!errors.programId}
                                                            onChange={(e) => { handleChange(e); this.updateFieldData(e) }}
                                                            onBlur={handleBlur}
                                                            required
                                                            multi
                                                            value={this.state.programId}
                                                        />
                                                        <FormFeedback>{errors.programId}</FormFeedback>
                                                    </Col>

                                                </FormGroup>
                                            </CardBody>
                                            <CardFooter>
                                                <Button type="submit" onClick={() => this.touchAll(setTouched, errors)} disabled={!isValid} size="sm" color="primary"><i className="fa fa-dot-circle-o"></i> Submit</Button>
                                                <Button type="reset" size="sm" color="danger"><i className="fa fa-ban"></i> Reset</Button>
                                            </CardFooter>
                                        </Form>
                                    )} />
                    </Card>
                </Col>
            </>
        )

    }

}
