import React, { Component } from "react";
import {
    Card, CardBody, CardHeader,
    Label, Input, FormGroup,
    CardFooter, Button, Col, Form
} from 'reactstrap';


const initialValues = {
    programName: '',
    realmId: '',
    realmCountryId: '',
    organisationId: '',
    programManagerUserId: '',
    airFreightPercentage: '',
    seaFreightPercentage: '',
    deliveredToRecivedLeadTime: '',
    draftToSubmittedLeadTime: '',
    planDraftLeadTime: '',
    submittedToApprovedLeadTime: '',
    approvedToShippedLeadTime: '',
    monthInFutureForAmc: '',
    monthInPastForAmc: '',
    healthAreaId: '',
    programNotes: ''
}

const validationSchema = function (values) {
    return Yup.object().shape({
        programName: Yup.string()
            .required("Please enter programName"),
        realmId: Yup.string()
            .required("Please select realm"),
        realmCountryId: Yup.string()
            .required('Please select country.'),
        organisationId: Yup.string()
            .required('Please select organisation'),
        programManagerUserId: Yup.string()
            .required('Please select program manager'),
        airFreightPercentage: Yup.string()
            .required('Please enter air freight percentage'),
        seaFreightPercentage: Yup.string()
            .required('Please enter sea freight percentage'),
        deliveredToRecivedLeadTime: Yup.string()
            .required('Please enter deliverd to recived lead time'),
        draftToSubmittedLeadTime: Yup.string()
            .required('Please enter draft to submitted lead time'),
        planDraftLeadTime: Yup.string()
            .required('Please enter plan to draft lead time'),
        submittedToApprovedLeadTime: Yup.string()
            .required('Please enter submit to approved lead time'),
        approvedToShippedLeadTime: Yup.string()
            .required('Please enter approved to shippedLeadTime'),
        monthInFutureForAmc: Yup.string()
            .required('Please enter month in funture for AMC'),
        healthAreaId: Yup.string()
            .required('Please enter month in past fro AMC'),
        programNotes: Yup.string()
            .required('Please enter notes'),


    })
}

const validate = (getValidationSchema) => {
    return (values) => {
        const validationSchema = getValidationSchema(values)
        try {
            validationSchema.validateSync(values, { abortEarly: false })
            return {}
        } catch (error) {
            return getErrorsFromValidationError(error)
        }
    }
}

const getErrorsFromValidationError = (validationError) => {
    const FIRST_ERROR = 0
    return validationError.inner.reduce((errors, error) => {
        return {
            ...errors,
            [error.path]: error.errors[FIRST_ERROR],
        }
    }, {})
}

export default class AddProgram extends Component {

    constructor(props) {
        super(props);
        this.state = {
            program: {
                label: {
                    label_en: ''
                },
                realm: {
                    realmId: ''
                },
                realmCountry: {
                    realmCountryId: ''
                },
                organisation: {
                    organisationId: ''
                },
                programManager: {
                    programManagerUserId: ''
                },
                airFreightPercentage: '',
                seaFreightPercentage: '',
                deliveredToRecivedLeadTime: '',
                draftToSubmittedLeadTime: '',
                planDraftLeadTime: '',
                submittedToApprovedLeadTime: '',
                approvedToShippedLeadTime: '',
                monthInFutureForAmc: '',
                monthInPastForAmc: '',
                healthAreaId: '',
                programNotes: ''
            }
        }
        this.dataChange = this.dataChange.bind(this);

    }
    componentDidMount() {


    }

    dataChange(event) {
        let { program } = this.state;
        if (event.target.name == "programName") {
            program.label.label_en = event.target.value;
        } if (event.target.name == "realmId") {
            program.realm.realmId = event.target.value;
        } if (event.target.name == 'realmCountryId') {
            program.realmCountry.realmCountryId = event.target.value;
        } if (event.target.name == 'organisationId') {
            program.organisation.organisationId = event.target.value;
        } if (event.target.name == 'airFreightPercentage') {
            program.airFreightPercentage = event.target.value;
        } if (event.target.name == 'seaFreightPercentage') {
            program.seaFreightPercentage = event.target.value;
        } if (event.target.name == 'deliveredToRecivedLeadTime') {
            program.deliveredToRecivedLeadTime = event.target.value;
        } if (event.target.name == 'draftToSubmittedLeadTime') {
            program.draftToSubmittedLeadTime = event.target.value;
        } if (event.target.name == 'planDraftLeadTime') {
            program.planDraftLeadTime = event.target.value;
        } if (event.target.name == 'submittedToApprovedLeadTime') {
            program.submittedToApprovedLeadTime = event.target.value;
        } if (event.target.name == 'approvedToShippedLeadTime') {
            program.approvedToShippedLeadTime = event.target.value;
        } if (event.target.name == 'monthInFutureForAmc') {
            program.monthInFutureForAmc = event.target.value;
        } if (event.target.name == 'monthInPastForAmc') {
            program.monthInPastForAmc = event.target.value;
        } if (event.target.name == 'healthAreaId') {
            program.health.healthAreaId = event.target.value;
        } else if (event.target.name == 'programNotes') {
            program.programNotes = event.target.value;
        }

        this.setState({ program }, () => { console.log("updated state--", this.state.program) })

    }
    touchAll(setTouched, errors) {
        setTouched({
            programName: true,
            realmId: true,
            realmCountryId: true,
            organisationId: true,
            programManagerUserId: true,
            airFreightPercentage: true,
            seaFreightPercentage: true,
            deliveredToRecivedLeadTime: true,
            draftToSubmittedLeadTime: true,
            planDraftLeadTime: true,
            submittedToApprovedLeadTime: true,
            approvedToShippedLeadTime: true,
            monthInFutureForAmc: true,
            monthInPastForAmc: true,
            healthAreaId: true,
            programNotes: true
        }
        )
        this.validateForm(errors)
    }
    validateForm(errors) {
        this.findFirstError('programForm', (fieldName) => {
            return Boolean(errors[fieldName])
        })
    }
    findFirstError(formName, hasError) {
        const form = document.forms[formName]
        for (let i = 0; i < form.length; i++) {
            if (hasError(form[i].name)) {
                form[i].focus()
                break
            }
        }
    }

    render() {
        return (
            <Col xs="12" sm="8">
                <Card>
                    <Formik
                        initialValues={initialValues}
                        validate={validate(validationSchema)}
                        onSubmit={(values, { setSubmitting, setErrors }) => {
                            BudgetServcie.addBudget(this.state.budget).then(response => {
                                this.props.history.push(`/budget/listBudgets/${response.data.message}`)
                            }
                            )
                                .catch(
                                    error => {
                                        switch (error.message) {
                                            case "Network Error":
                                                this.setState({
                                                    message: error.message
                                                })
                                                break
                                            default:
                                                this.setState({
                                                    message: error.message
                                                })
                                                break
                                        }
                                    }
                                )
                        }}
                        render={
                            ({
                                values,
                                errors,
                                touched,
                                handleChange,
                                handleBlur,
                                handleSubmit,
                                isSubmitting,
                                isValid,
                                setTouched
                            }) => (

                                    <Form>
                                        <CardHeader>
                                            <strong>Add Program</strong>
                                        </CardHeader>
                                        <CardBody>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Select Realm</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="select" name="realmId" id="realmId" onChange={this.updateFieldData}>
                                                        <option value="0">Please select</option>
                                                        <option value="1">Realm #1</option>
                                                        <option value="2">Realm #2</option>
                                                        <option value="3">Realm #3</option>
                                                    </Input>
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Select Country</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="select" name="realmCountryId" id="realmCountryId" onChange={this.updateFieldData}>
                                                        <option value="0">Please select</option>
                                                        <option value="1">Country #1</option>
                                                        <option value="2">Country #2</option>
                                                        <option value="3">Country #3</option>
                                                    </Input>
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Select Organisation</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="select" name="organisationId" id="organisationId" onChange={this.updateFieldData}>
                                                        <option value="0">Please select</option>
                                                        <option value="1">product #1</option>
                                                        <option value="2">product #2</option>
                                                        <option value="3">product #3</option>
                                                    </Input>
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Select Health Area</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="select" name="healthAreaId" id="healthAreaId" onChange={this.updateFieldData}>
                                                        <option value="0">Please select</option>
                                                        <option value="1">Health Area #1</option>
                                                        <option value="2">Health Area #2</option>
                                                        <option value="3">Health Area #3</option>
                                                    </Input>
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Select Program Manager</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="select" name="programManagerUserId" id="programManagerUserId" onChange={this.updateFieldData}>
                                                        <option value="0">Please select</option>
                                                        <option value="1">product #1</option>
                                                        <option value="2">product #2</option>
                                                        <option value="3">product #3</option>
                                                    </Input>
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="4">
                                                    <Label htmlFor="select">Program Notes</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="textarea" name="programNotes" id="programNotes" onChange={this.updateFieldData} />

                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Air Freight Percentage</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="text" name="airFreightPercentage" id="airFreightPercentage" placeholder="Enter air freight percentage" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Sea Freight Percentage</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="text" name="seaFreightPercentage" id="seaFreightPercentage" placeholder="Enter sea freight percentage" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Plan Draft Lead Time</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="planDraftLeadTime" id="planDraftLeadTime" placeholder="Enter plan to draft lead time" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Draft To Submitted Lead Time</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="draftToSubmittedLeadTime" id="draftToSubmittedLeadTime" placeholder="Enter draft to submitted lead time" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Submitted To Approved Lead Time</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="submittedToApprovedLeadTime" id="submittedToApprovedLeadTime" placeholder="Enter submited to approved lead time" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Approve To Shipped Lead Time</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="approvedToShippedLeadTime" id="approvedToShippedLeadTime" placeholder="Enter draft to submitted lead time" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Delivered To Recived Lead Time</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="deliveredToRecivedLeadTime" id="deliveredToRecivedLeadTime" placeholder="Enter delivered to reacived lead time" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Month In Past For AMC</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="monthInPastForAmc" id="monthInPastForAmc" placeholder="Enter month in past for AMC" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>
                                            <FormGroup>
                                                <Col md="5">
                                                    <Label htmlFor="company">Month In Future For AMC</Label>
                                                </Col>
                                                <Col xs="12" md="9">
                                                    <Input type="number" name="monthInFutureForAmc" id="monthInFutureForAmc" placeholder="Enter month in future for AMC" onChange={this.updateFieldData} />
                                                </Col>
                                            </FormGroup>

                                        </CardBody>
                                        <CardFooter>
                                            <Button type="button" size="sm" color="primary" onClick={this.submitForm}><i className="fa fa-dot-circle-o"></i>Submit </Button>
                                        </CardFooter>
                                    </Form>
                                )} />
                </Card>
            </Col>

        );
    }
}
